// SVG描画関連の関数
import React from 'react';
import { SVG_CONFIG, ANALYSIS_VIEW_CONFIG } from './RocketConstants';

// 安全な値を取得するユーティリティ関数
export const getSafeValue = (value, defaultValue = 0) => {
  return isNaN(value) ? defaultValue : value;
};

// SVG描画関数
export const getNosePath = (config, noseShape, noseHeight, bodyWidth) => {
  const startX = config.centerX - bodyWidth / 2;
  const endX = config.centerX + bodyWidth / 2;
  const baseY = config.height - bodyHeight;
  const topY = baseY - noseHeight;
  if (noseShape === "cone") {
    // 円錐ノーズ
    return `M ${startX} ${baseY} L ${config.centerX} ${topY} L ${endX} ${baseY} Z`;
  } else if (noseShape === "parabola") {
    // 放物線ノーズの修正 - y=x^2型の曲線を再現
    // SVGパスでは3次ベジェ曲線を使って、より正確な放物線形状を作成
    const controlY = topY + noseHeight * 0.15; // より浅い制御点で膨らみを作る
    return `M ${startX} ${baseY} 
            C ${startX} ${controlY}, ${config.centerX} ${topY}, ${config.centerX} ${topY}
            C ${config.centerX} ${topY}, ${endX} ${controlY}, ${endX} ${baseY} Z`;
  } else {
    // オジブノーズ - ボディの幅を超えないように制限
    const halfWidth = bodyWidth / 2;
    return `M ${startX} ${baseY}
            Q ${startX + halfWidth * 0.1} ${topY + noseHeight * 0.4}, ${config.centerX} ${topY}
            Q ${endX - halfWidth * 0.1} ${topY + noseHeight * 0.4}, ${endX} ${baseY} Z`;
  }
};

// 座標を指定して描画するバージョン
export const getShapeNosePath = (x, y, params, noseShape) => {
  const startX = x - params.bodyWidth / 2;
  const endX = x + params.bodyWidth / 2;
  const baseY = y - params.bodyHeight;
  const topY = baseY - params.noseHeight;
  if (noseShape === "cone") {
    // 円錐ノーズ
    return `M ${startX} ${baseY} L ${x} ${topY} L ${endX} ${baseY} Z`;
  } else if (noseShape === "parabola") {
    // 放物線ノーズの修正 - y=x^2型の曲線を再現
