                <line 
                  x1={400} 
                  y1={550} 
                  x2={400} 
                  y2={550 - 0.65 * getSafeNumber(rocketSim.trajectoryScale, 10)} 
                  stroke="#374151" 
                  strokeWidth="2" 
                />
              </g>

              {/* 過去のフライト軌跡 - データ間引きを減らして密度を上げる */}
              {(rocketSim.completedFlights || []).map((flight, index) => (
                <path
                  key={`completed-flight-${index}`}
                  d={`M 400 550 ` + 
                    (flight.data || [])
                      .filter((d, i) => i % 2 === 0) // データ間引きをさらに減らす
                      .map(d => {
                        const x = 400 + getSafeNumber(d.physicsX) * getSafeNumber(flight.scale, 10);
                        const y = 550 - getSafeNumber(d.physicsY) * getSafeNumber(flight.scale, 10);
                        return `L ${x} ${y}`;
                      })
                      .join(' ')}
                  stroke="rgba(107, 114, 128, 0.5)"
                  strokeWidth="1.5"
                  fill="none"
                  strokeDasharray="2 2"
                />
              ))}

              {/* 現在のフライト軌跡 - 色を鮮やかに、線を太く */}
              {rocketSim.isLaunched && (rocketSim.flightData || []).length > 0 && (
                <path
                  d={`M 400 550 ` + 
                    (rocketSim.flightData || [])
                      .filter((d, i) => i <= Math.floor(getSafeNumber(rocketSim.currentTime) / 0.02) && i % 2 === 0)
                      .map(d => {
                        // NaNチェックを追加
                        const x = isNaN(d.physicsX) ? 400 : 400 + d.physicsX * getSafeNumber(rocketSim.trajectoryScale, 10);
                        const y = isNaN(d.physicsY) ? 550 : 550 - d.physicsY * getSafeNumber(rocketSim.trajectoryScale, 10);
                        return `L ${x} ${y}`;
                      })
                      .join(' ')}
                  stroke="#2563eb"
                  strokeWidth="2.5"
                  fill="none"
                />
              )}

              {/* ロケットとパラシュートの表示 - パラシュート表示を改良 */}
              {rocketSim.isLaunched && (rocketSim.flightData || []).length > 0 && (
                <>
                  {/* パラシュート表示（パラシュート展開時のみ） */}
                  {safePosition.isParachuteEjected && rocketSim.getParachuteStringPaths && rocketSim.getParachutePath && (
                    <>
                      <path
                        d={rocketSim.getParachuteStringPaths(
                          metersToSvgX(safePosition.physicsX || 0), 
                          metersToSvgY(safePosition.physicsY || 0), 
                          safePosition.isParachuteActive || false, 
                          getSafeNumber(safePosition.parachuteDeploymentProgress),
                          safeRotation
                        )}
                        stroke="#374151"
                        strokeWidth="1"
                        fill="none"
                      />
                      <path
                        d={rocketSim.getParachutePath(
                          metersToSvgX(safePosition.physicsX || 0), 
                          metersToSvgY(safePosition.physicsY || 0), 
                          safePosition.isParachuteActive || false, 
                          getSafeNumber(safePosition.parachuteDeploymentProgress),
                          safeRotation
                        )}
                        fill={safePosition.isParachuteActive ? "#FFB300" : "#9ca3af"}
                        stroke="#374151"
                        strokeWidth="2"
                      />
                    </>
                  )}

                  {/* ロケット本体 */}
                  <g transform={rocketSim.getSafeRotationTransform ? 
                    rocketSim.getSafeRotationTransform(
                      safeRotation, 
                      metersToSvgX(safePosition.physicsX || 0), 
                      metersToSvgY(safePosition.physicsY || 0)
                    ) : `translate(${metersToSvgX(safePosition.physicsX || 0)}, ${metersToSvgY(safePosition.physicsY || 0)}) rotate(${safeRotation})`}>
                    {rocketSim.getBodyPath && (
                      <path 
                        d={rocketSim.getBodyPath(
                          {
                            height: 0,
                            centerX: 0
                          }, 
                          rocketDisplayParams.bodyHeight,
                          rocketDisplayParams.bodyWidth
                        )} 
                        fill="#9CA3AF" 
                        stroke="#374151" 
                      />
                    )}

                    {rocketSim.getLeftFinPath && (
                      <path 
                        d={rocketSim.getLeftFinPath(
                          {
                            height: 0,
                            centerX: 0
                          }, 
                          rocketDisplayParams.bodyWidth,
                          rocketDisplayParams.finHeight,
                          rocketDisplayParams.finBaseWidth,
                          rocketDisplayParams.finSweepLength,
                          rocketDisplayParams.finTipWidth
                        )} 
                        fill="#6B7280" 
                        stroke="#374151" 
                      />
                    )}

                    {rocketSim.getRightFinPath && (
                      <path 
                        d={rocketSim.getRightFinPath(
                          {
                            height: 0,
                            centerX: 0
                          }, 
                          rocketDisplayParams.bodyWidth,
                          rocketDisplayParams.finHeight,
                          rocketDisplayParams.finBaseWidth,
                          rocketDisplayParams.finSweepLength,
                          rocketDisplayParams.finTipWidth
                        )} 
                        fill="#6B7280" 
                        stroke="#374151" 
                      />
                    )}

                    {rocketSim.getCenterFinsPath && (
                      <path 
                        d={rocketSim.getCenterFinsPath(
                          {
                            height: 0,
                            centerX: 0
                          }, 
                          rocketDisplayParams.finThickness,
                          rocketDisplayParams.finBaseWidth,
                          rocketDisplayParams.finSweepLength,
                          rocketDisplayParams.finTipWidth
                        )} 
                        fill="#6B7280" 
                        stroke="#374151" 
                      />
                    )}

                    {rocketSim.getNosePath && (
                      <path 
                        d={rocketSim.getNosePath(
                          {
                            height: 0,
                            centerX: 0
                          }, 
                          rocketSim.noseShape || "cone",
                          rocketDisplayParams.noseHeight,
                          rocketDisplayParams.bodyWidth,
                          rocketDisplayParams.bodyHeight
                        )} 
                        fill="#D1D5DB" 
                        stroke="#374151" 
                      />
                    )}
                  </g>
                </>
              )}

              {/* 未発射時のロケット表示 */}
              {!rocketSim.isLaunched && (
                <g transform={`rotate(${getSafeNumber(rocketSim.launchAngle)} 400 550)`}>
                  {/* ボディ */}
                  <rect
                    x={400 - rocketDisplayParams.bodyWidth / 2}
                    y={550 - rocketDisplayParams.bodyHeight}
                    width={rocketDisplayParams.bodyWidth}
                    height={rocketDisplayParams.bodyHeight}
                    fill="#9CA3AF"
                    stroke="#374151"
                    strokeWidth="1"
                  />

                  {/* ノーズ */}
                  <path
                    d={`M ${400 - rocketDisplayParams.bodyWidth / 2} ${550 - rocketDisplayParams.bodyHeight} 
                        L ${400} ${550 - rocketDisplayParams.bodyHeight - rocketDisplayParams.noseHeight} 
                        L ${400 + rocketDisplayParams.bodyWidth / 2} ${550 - rocketDisplayParams.bodyHeight} Z`}
                    fill="#D1D5DB"
                    stroke="#374151"
                    strokeWidth="1"
                  />

                  {/* 左フィン */}
                  <path
                    d={`M ${400 - rocketDisplayParams.bodyWidth / 2} ${550 - rocketDisplayParams.finBaseWidth}
                        L ${400 - rocketDisplayParams.bodyWidth / 2} ${550}
                        L ${400 - rocketDisplayParams.bodyWidth / 2 - rocketDisplayParams.finHeight} ${550 + rocketDisplayParams.finSweepLength}
                        L ${400 - rocketDisplayParams.bodyWidth / 2 - rocketDisplayParams.finHeight} ${550 + rocketDisplayParams.finSweepLength - rocketDisplayParams.finTipWidth} Z`}
                    fill="#6B7280"
                    stroke="#374151"
                    strokeWidth="1"
                  />

                  {/* 右フィン */}
                  <path
                    d={`M ${400 + rocketDisplayParams.bodyWidth / 2} ${550 - rocketDisplayParams.finBaseWidth}
                        L ${400 + rocketDisplayParams.bodyWidth / 2} ${550}
                        L ${400 + rocketDisplayParams.bodyWidth / 2 + rocketDisplayParams.finHeight} ${550 + rocketDisplayParams.finSweepLength}
                        L ${400 + rocketDisplayParams.bodyWidth / 2 + rocketDisplayParams.finHeight} ${550 + rocketDisplayParams.finSweepLength - rocketDisplayParams.finTipWidth} Z`}
                    fill="#6B7280"
                    stroke="#374151"
                    strokeWidth="1"
                  />

                  {/* センターフィン */}
                  <rect
                    x={400 - rocketDisplayParams.finThickness / 2}
                    y={550 - rocketDisplayParams.finBaseWidth}
                    width={rocketDisplayParams.finThickness}
                    height={rocketDisplayParams.finBaseWidth}
                    fill="#6B7280"
                    stroke="#374151"
                    strokeWidth="1"
                  />
                </g>
              )}

              {/* デバッグ情報表示 */}
              {debugView && (
                <g>
                  <rect
                    x={10}
                    y={10}
                    width={280}
                    height={380}
                    fill="rgba(255,255,255,0.8)"
                    stroke="#666"
                    strokeWidth="1"
                  />
                  <text x={20} y={30} fontSize="12" fill="#333">
                    時間: {getSafeNumber(rocketSim.currentTime).toFixed(2)} 秒
                  </text>
                  <text x={20} y={50} fontSize="12" fill="#333">
                    高度: {getSafeNumber(rocketSim.currentHeight).toFixed(2)} m
                  </text>
                  <text x={20} y={70} fontSize="12" fill="#333">
                    速度: {getSafeNumber(rocketSim.currentSpeed).toFixed(2)} m/s
                  </text>
                  <text x={20} y={90} fontSize="12" fill="#333">
                    角度: {(safeRotation % 360).toFixed(2)}°
                  </text>
                  <text x={20} y={110} fontSize="12" fill="#333">
                    現在水平距離: {getSafeNumber(rocketSim.currentDistance).toFixed(2)} m
                  </text>
                  <text x={20} y={130} fontSize="12" fill="#333">
                    フェーズ: {rocketSim.getCurrentFlightPhase ? rocketSim.getCurrentFlightPhase() : "不明"}
                  </text>
                  <text x={20} y={150} fontSize="12" fill="#333">
                    パラシュート展開率: {getSafeNumber(safePosition.parachuteDeploymentProgress).toFixed(2)}
                  </text>
                  <text x={20} y={170} fontSize="12" fill="#333">
                    アニメーション速度: 0.2倍速
                  </text>
                  <text x={20} y={190} fontSize="12" fill="#333">
                    推力終了時高度: {(rocketSim.keyPoints?.thrustEnd?.height || 0).toFixed(2) || "N/A"} m
                  </text>
                  <text x={20} y={210} fontSize="12" fill="#333">
                    横風速度: {safeWindSpeed.toFixed(2)} m/s
                  </text>
                  {safePosition.effectiveWindSpeed !== undefined && (
                    <text x={20} y={230} fontSize="12" fill="#333">
                      実効風速: {getSafeNumber(safePosition.effectiveWindSpeed).toFixed(2)} m/s
                    </text>
                  )}
                  <text x={20} y={250} fontSize="12" fill="#333">
                    最大水平距離: {getSafeNumber(rocketSim.currentMaxDistance).toFixed(2)} m
                  </text>
                  <text x={20} y={270} fontSize="12" fill="#333">
                    表示スケール: {getSafeNumber(rocketSim.trajectoryScale, 10).toFixed(2)} px/m
                  </text>
                  <text x={20} y={290} fontSize="12" fill="#333">
                    ロケットスケール: {rocketScale.toFixed(5)}
                  </text>
                  <text x={20} y={310} fontSize="12" fill="#333">
                    フィンたわみ量: {getSafeNumber(safePosition.finDeflection).toFixed(2)} mm
                  </text>
                  {safePosition.torque !== undefined && (
                    <text x={20} y={330} fontSize="12" fill="#333">
                      トルク: {getSafeNumber(safePosition.torque).toFixed(6)}
                    </text>
                  )}
                  <text x={20} y={350} fontSize="12" fill="#333">
                    風向き: {safeWindSpeed > 0 ? "左→右" : safeWindSpeed < 0 ? "右→左" : "無風"}
                  </text>
                  <text x={20} y={370} fontSize="12" fill="#333">
                    角度制限: {safeWindSpeed !== 0 ? `${-Math.sign(safeWindSpeed) * 90}°` : "なし"}
                  </text>
                </g>
              )}
            </svg>
          </div>
          {/* Right side: New circular attitude display window */}
<div className="w-1/4 bg-white rounded-lg border p-4 flex flex-col items-center">
  <h4 className="font-semibold mb-2">ロケット姿勢表示</h4>

  {/* Circular window */}
  <div className="relative">
    <svg 
      width={200} 
      height={200} 
      viewBox="-100 -100 200 200"
      className="border-2 border-gray-300 rounded-full overflow-hidden bg-gray-50"
    >
      {/* Background circle */}
      <circle cx="0" cy="0" r="98" fill="#f0f0f0" />

      {/* Angle markings */}
      {[...Array(12)].map((_, i) => {
        const angle = i * 30;
        const radians = (angle - 90) * Math.PI / 180;
        const x1 = Math.cos(radians) * 80;
        const y1 = Math.sin(radians) * 80;
        const x2 = Math.cos(radians) * 90;
        const y2 = Math.sin(radians) * 90;

        return (
          <g key={`mark-${i}`}>
            <line 
              x1={x1} 
              y1={y1} 
              x2={x2} 
              y2={y2} 
              stroke="#999" 
              strokeWidth="1" 
            />
            {i % 3 === 0 && (
              <text 
                x={Math.cos(radians) * 70} 
                y={Math.sin(radians) * 70} 
                textAnchor="middle" 
                dominantBaseline="middle"
                fontSize="10"
                fill="#666"
              >
                {angle}°
              </text>
            )}
          </g>
        );
      })}

      {/* Horizontal and vertical lines */}
      <line x1="-90" y1="0" x2="90" y2="0" stroke="#666" strokeWidth="1" strokeDasharray="3,2" />
      <line x1="0" y1="-90" x2="0" y2="90" stroke="#666" strokeWidth="1" strokeDasharray="3,2" />

      {/* Rocket - showing attitude angle (center of gravity as rotation center) */}
      <g transform={`rotate(${isNaN(position.rotation) ? 0 : position.rotation})`}>
        {/* Rocket body - Y position adjusted so center of gravity is at origin */}
        <g transform={`translate(0, ${-Math.max(0, Math.min(100, getSafeNumber(cogToBottom, 20)))})`}>
          {/* Body */}
          <rect
            x={-Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.bodyWidth, 10) / 2))}
            y={-Math.max(10, Math.min(150, getSafeNumber(safeAttitudeRocketParams.bodyHeight, 60)))}
            width={Math.max(2, Math.min(60, getSafeNumber(safeAttitudeRocketParams.bodyWidth, 10)))}
            height={Math.max(20, Math.min(300, getSafeNumber(safeAttitudeRocketParams.bodyHeight, 60)))}
            fill="#9CA3AF"
            stroke="#374151"
            strokeWidth="0.5"
          />

          {/* Nose - direct path specification with safety checks */}
          <path
            d={`M ${-Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.bodyWidth, 10) / 2))} 
                  ${-Math.max(10, Math.min(150, getSafeNumber(safeAttitudeRocketParams.bodyHeight, 60)))} 
                L 0 
                  ${-Math.max(10, Math.min(150, getSafeNumber(safeAttitudeRocketParams.bodyHeight, 60))) 
                    - Math.max(5, Math.min(50, getSafeNumber(safeAttitudeRocketParams.noseHeight, 20)))} 
                L ${Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.bodyWidth, 10) / 2))} 
                  ${-Math.max(10, Math.min(150, getSafeNumber(safeAttitudeRocketParams.bodyHeight, 60)))} Z`}
            fill="#D1D5DB"
            stroke="#374151"
            strokeWidth="0.5"
          />

          {/* Left fin - with safety checks */}
          <path
            d={`M ${-Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.bodyWidth, 10) / 2))} 
                  ${-Math.max(5, Math.min(50, getSafeNumber(safeAttitudeRocketParams.finBaseWidth, 15)))}
                L ${-Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.bodyWidth, 10) / 2))} 0
                L ${-Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.bodyWidth, 10) / 2)) 
                   - Math.max(5, Math.min(50, getSafeNumber(safeAttitudeRocketParams.finHeight, 15)))} 
                  ${Math.max(0, Math.min(50, getSafeNumber(safeAttitudeRocketParams.finSweepLength, 10)))}
                L ${-Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.bodyWidth, 10) / 2)) 
                   - Math.max(5, Math.min(50, getSafeNumber(safeAttitudeRocketParams.finHeight, 15)))} 
                  ${Math.max(0, Math.min(50, getSafeNumber(safeAttitudeRocketParams.finSweepLength, 10))) 
                    - Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.finTipWidth, 5)))} Z`}
            fill="#6B7280"
            stroke="#374151"
            strokeWidth="0.5"
          />

          {/* Right fin - with safety checks */}
          <path
            d={`M ${Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.bodyWidth, 10) / 2))} 
                  ${-Math.max(5, Math.min(50, getSafeNumber(safeAttitudeRocketParams.finBaseWidth, 15)))}
                L ${Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.bodyWidth, 10) / 2))} 0
                L ${Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.bodyWidth, 10) / 2)) 
                   + Math.max(5, Math.min(50, getSafeNumber(safeAttitudeRocketParams.finHeight, 15)))} 
                  ${Math.max(0, Math.min(50, getSafeNumber(safeAttitudeRocketParams.finSweepLength, 10)))}
                L ${Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.bodyWidth, 10) / 2)) 
                   + Math.max(5, Math.min(50, getSafeNumber(safeAttitudeRocketParams.finHeight, 15)))} 
                  ${Math.max(0, Math.min(50, getSafeNumber(safeAttitudeRocketParams.finSweepLength, 10))) 
                    - Math.max(1, Math.min(30, getSafeNumber(safeAttitudeRocketParams.finTipWidth, 5)))} Z`}
            fill="#6B7280"
            stroke="#374151"
            strokeWidth="0.5"
          />

          {/* Center fin - with safety checks */}
          <rect
            x={-Math.max(0.5, Math.min(5, getSafeNumber(safeAttitudeRocketParams.finThickness, 1) / 2))}
            y={-Math.max(5, Math.min(50, getSafeNumber(safeAttitudeRocketParams.finBaseWidth, 15)))}
            width={Math.max(1, Math.min(10, getSafeNumber(safeAttitudeRocketParams.finThickness, 1)))}
            height={Math.max(5, Math.min(100, getSafeNumber(safeAttitudeRocketParams.finBaseWidth, 15) + 
                   Math.max(0, getSafeNumber(safeAttitudeRocketParams.finSweepLength, 10))))}
            fill="#6B7280"
            stroke="#374151"
            strokeWidth="0.5"
          />

          {/* Center of gravity marker */}
          <circle cx="0" cy="0" r="2" fill="red" />
        </g>
      </g>

      {/* Current angle display */}
      <text 
        x="0" 
        y="-85" 
        textAnchor="middle" 
        fontSize="12" 
        fill="#333"
        fontWeight="bold"
      >
        {((getSafeNumber(position.rotation, 0) % 360) || 0).toFixed(1)}°
      </text>
    </svg>
  </div>

  {/* Attitude-related additional information */}
  <div className="mt-4 text-sm">
    <div className="grid grid-cols-2 gap-x-2 gap-y-1">
      <div className="font-semibold">飛行フェーズ:</div>
      <div>{rocketSim.getCurrentFlightPhase ? rocketSim.getCurrentFlightPhase() : "初期状態"}</div>

      <div className="font-semibold">発射角度:</div>
      <div>{getSafeNumber(rocketSim.launchAngle, 0)}°</div>

      <div className="font-semibold">現在角度:</div>
      <div>{(getSafeNumber(position.rotation, 0) % 360).toFixed(1)}°</div>

      <div className="font-semibold">角度変化:</div>
      <div>{(getSafeNumber(position.rotation, 0) - getSafeNumber(rocketSim.launchAngle, 0)).toFixed(1)}°</div>

      <div className="font-semibold">風向き:</div>
      <div>{rocketSim.windSpeed > 0 ? "右" : rocketSim.windSpeed < 0 ? "左" : "なし"}</div>
    </div>
  </div>

  {/* Previous flight results */}
  {rocketSim.lastFlightResults && (
    <LastFlightResults results={rocketSim.lastFlightResults} />
  )}
</div>

          {/* デバッグ表示 */}
          {debugView && (
            <WindAngleLimitVisualizer 
              position={position}
              windSpeed={rocketSim.windSpeed}
              currentFlightPhase={rocketSim.getCurrentFlightPhase()}
            />
          )}
        </div>

        <div className="bg-white rounded-lg shadow-sm mt-4 p-4 border border-gray-200">
          <h4 className="font-semibold mb-2">使用パラメータ</h4>
          <div className="grid grid-cols-2 gap-4 text-sm">
            <ul className="list-disc pl-5">
              <li>重量: {rocketSim.weight} g</li>
              <li>モーター: {rocketSim.selectedMotor}</li>
              <li>パラシュート: {rocketSim.selectedParachute}</li>
              <li>ノーズ形状: {NOSE_SHAPES[rocketSim.noseShape]?.name || "不明"}</li>
              <li>抗力係数: {(NOSE_SHAPES[rocketSim.noseShape]?.cd || 0).toFixed(2)}</li>
            </ul>
            <ul className="list-disc pl-5">
              <li>ロケット全長: {getSafeNumber(rocketSim.calculations?.totalHeight || 0)} mm</li>
              <li>ボディ直径: {getSafeNumber(rocketSim.bodyWidth)} mm</li>
              <li>フィン翼幅: {getSafeNumber(rocketSim.finHeight)} mm</li>
              <li>フィン厚み: {getSafeNumber(rocketSim.finThickness, 0).toFixed(1)} mm</li>
              <li>フィン材質: {FIN_MATERIALS[rocketSim.finMaterial]?.name || "不明"}</li>
              <li>重心位置: {getSafeNumber(rocketSim.centerOfGravity)} mm</li>
              <li>風速プロファイル: {WIND_PROFILES[rocketSim.windProfile]?.name || "不明"}</li>
            </ul>
          </div>
        </div>
      </div>

      {/* 結果ポップアップ */}
      {rocketSim.showResultsPopup && (
        <ResultsPopup 
          results={rocketSim.flightResults} 
          onClose={rocketSim.handleCloseResultsPopup} 
        />
      )}
    </div>
  );
};
