                  strokeWidth="1.5"
                />

                {/* 右フィン - 縦向きに修正 */}
                <path
                  d={`M ${(rocketSim.bodyWidth || 30) / 2} ${-(rocketSim.finBaseWidth || 30)}
                      L ${(rocketSim.bodyWidth || 30) / 2} ${0}
                      L ${(rocketSim.bodyWidth || 30) / 2 + (rocketSim.finHeight || 40)} ${rocketSim.finSweepLength || 0}
                      L ${(rocketSim.bodyWidth || 30) / 2 + (rocketSim.finHeight || 40)} ${(rocketSim.finSweepLength || 0) - (rocketSim.finTipWidth || 20)} Z`}
                  fill="#6B7280"
                  stroke="#374151"
                  strokeWidth="1.5"
                />

                {/* センターフィン - 縦向きに修正 */}
                <rect
                  x={-(rocketSim.finThickness || 2) / 2}
                  y={-(rocketSim.finBaseWidth || 30)}
                  width={rocketSim.finThickness || 2}
                  height={(rocketSim.finBaseWidth || 30) + Math.max(0, rocketSim.finSweepLength || 0)}
                  fill="#6B7280"
                  stroke="#374151"
                  strokeWidth="1.5"
                />

                {/* 重心マーカー - ノーズ先端からの距離で修正 */}
                <line
                  x1={-60}
                  y1={-(rocketSim.bodyHeight || 200) - (rocketSim.noseHeight || 50) + (rocketSim.centerOfGravity || 0)}
                  x2={60}
                  y2={-(rocketSim.bodyHeight || 200) - (rocketSim.noseHeight || 50) + (rocketSim.centerOfGravity || 0)}
                  stroke="#EF4444"
                  strokeWidth="2.5"
                />

                {/* 圧力中心マーカー - ノーズ先端からの距離で修正 */}
                <line
                  x1={-60}
                  y1={-(rocketSim.bodyHeight || 200) - (rocketSim.noseHeight || 50) + (safeCalculations.pressureCenter || 0)}
                  x2={60}
                  y2={-(rocketSim.bodyHeight || 200) - (rocketSim.noseHeight || 50) + (safeCalculations.pressureCenter || 0)}
                  stroke="#3B82F6"
                  strokeWidth="2"
                  strokeDasharray="6,3"
                />

                {/* 静安定マージン用圧力中心マーカー - ノーズ先端からの距離で修正 */}
                <line
                  x1={-60}
                  y1={-(rocketSim.bodyHeight || 200) - (rocketSim.noseHeight || 50) + (safeCalculations.stabilityCenterOfPressure || 0)}
                  x2={60}
                  y2={-(rocketSim.bodyHeight || 200) - (rocketSim.noseHeight || 50) + (safeCalculations.stabilityCenterOfPressure || 0)}
                  stroke="#10B981"
                  strokeWidth="2"
                  strokeDasharray="3,3"
                />
              </g>
            </svg>
          </div>
        </div>
      </div>
    </div>
  );
};

// シミュレーションタブコンポーネント
const SimulationTab = ({ rocketSim, debugView, setDebugView }) => {

  const getSafeHandler = (handler) => {
    return handler || (() => {});
  };

  // rocketSimの初期化が完了したかどうかをチェック
  if (!rocketSim || !rocketSim.isInitialized) {
    // 初期化が完了していない場合はローディング表示
    return (
      <div className="bg-white rounded-lg shadow-lg p-6 flex justify-center items-center">
        <div className="text-center">
          <div className="text-lg font-semibold mb-2">設定を読み込み中...</div>
          <div className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full animate-spin mx-auto"></div>
        </div>
      </div>
    );
  }

  // 現在の位置情報を安全に取得
  const position = rocketSim.getCurrentPosition ? rocketSim.getCurrentPosition() : {};

  // 各種パラメータの安全な取得
  const safePosition = position || {};
  const safeWindSpeed = getSafeNumber(rocketSim.windSpeed);
  const safeRotation = getSafeNumber(safePosition.rotation);
  const windArrow = rocketSim.getWindArrow ? rocketSim.getWindArrow(safeWindSpeed) : null;

  // rocketSimデータの有効性をチェックする関数
  const isRocketDataValid = React.useMemo(() => {
    // rocketSimオブジェクトが存在し、必要なプロパティが数値型かチェック
    return !!rocketSim && 
           typeof rocketSim.bodyWidth === 'number' && !isNaN(rocketSim.bodyWidth) &&
           typeof rocketSim.bodyHeight === 'number' && !isNaN(rocketSim.bodyHeight) &&
           typeof rocketSim.noseHeight === 'number' && !isNaN(rocketSim.noseHeight) &&
           typeof rocketSim.finHeight === 'number' && !isNaN(rocketSim.finHeight) &&
           typeof rocketSim.finBaseWidth === 'number' && !isNaN(rocketSim.finBaseWidth) &&
           typeof rocketSim.finTipWidth === 'number' && !isNaN(rocketSim.finTipWidth) &&
           typeof rocketSim.finThickness === 'number' && !isNaN(rocketSim.finThickness) &&
           typeof rocketSim.finSweepLength === 'number' && !isNaN(rocketSim.finSweepLength) &&
           typeof rocketSim.centerOfGravity === 'number' && !isNaN(rocketSim.centerOfGravity) &&
           typeof rocketSim.getActualRocketDimensions === 'function';
  }, [rocketSim]);

  // 姿勢表示のパラメータを計算する
  const attitudeDisplayParams = React.useMemo(() => {
    // データが無効な場合はデフォルト値を返す
    if (!isRocketDataValid) {
      return {
        attitudeDisplayScale: 0.3,
        safeAttitudeRocketParams: {
          bodyWidth: 5,
          bodyHeight: 30,
          noseHeight: 10,
          finHeight: 5,
          finBaseWidth: 10,
          finTipWidth: 5,
          finThickness: 0.5,
          finSweepLength: 5
        },
        cogY: 15,
        cogToBottom: 15
      };
    }

    try {
      // 円の表示エリアの大きさに基づいて計算
      const circleRadius = 90; // 円の有効半径（余白含む）

      // getActualRocketDimensions呼び出しを安全に行う
      let totalHeight = 312; // デフォルト値
      try {
        const dimensions = rocketSim.getActualRocketDimensions({ 
          height: 100, 
          centerX: 50 
        });
        totalHeight = getSafeNumber(dimensions.totalHeight, 312);
      } catch (error) {
        console.error("姿勢表示用の寸法計算でエラー:", error);
      }

      // スケール計算を安全に行う
      const attitudeDisplayScale = getSafeNumber((circleRadius * 1.6) / totalHeight, 0.3);

      // 姿勢表示用のロケットパラメータ - すべての値に安全対策を追加
      const safeAttitudeRocketParams = {
        bodyWidth: getSafeNumber(rocketSim.bodyWidth * attitudeDisplayScale, 5),
        bodyHeight: getSafeNumber(rocketSim.bodyHeight * attitudeDisplayScale, 30),
        noseHeight: getSafeNumber(rocketSim.noseHeight * attitudeDisplayScale, 10),
        finHeight: getSafeNumber(rocketSim.finHeight * attitudeDisplayScale, 5),
        finBaseWidth: getSafeNumber(rocketSim.finBaseWidth * attitudeDisplayScale, 10),
        finTipWidth: getSafeNumber(rocketSim.finTipWidth * attitudeDisplayScale, 5),
        finThickness: getSafeNumber(rocketSim.finThickness * attitudeDisplayScale, 0.5),
        finSweepLength: getSafeNumber(rocketSim.finSweepLength * attitudeDisplayScale, 5)
      };

      // 重心位置の計算（スケールに合わせて）
      const cogY = getSafeNumber(rocketSim.centerOfGravity * attitudeDisplayScale, 15);

      // 重心位置に基づいて回転中心を計算
      const cogToBottom = getSafeNumber(
        safeAttitudeRocketParams.bodyHeight + safeAttitudeRocketParams.noseHeight - cogY, 
        15
      );

      return {
        attitudeDisplayScale,
        safeAttitudeRocketParams,
        cogY,
        cogToBottom
      };
    } catch (error) {
      console.error("姿勢表示パラメータの計算でエラー:", error);
      return {
        attitudeDisplayScale: 0.3,
        safeAttitudeRocketParams: {
          bodyWidth: 5,
          bodyHeight: 30,
          noseHeight: 10,
          finHeight: 5,
          finBaseWidth: 10,
          finTipWidth: 5,
          finThickness: 0.5,
          finSweepLength: 5
        },
        cogY: 15,
        cogToBottom: 15
      };
    }
  }, [
    isRocketDataValid,
    rocketSim.bodyWidth,
    rocketSim.bodyHeight,
    rocketSim.noseHeight,
    rocketSim.finHeight,
    rocketSim.finBaseWidth,
    rocketSim.finTipWidth,
    rocketSim.finThickness,
    rocketSim.finSweepLength,
    rocketSim.centerOfGravity,
    rocketSim.getActualRocketDimensions
  ]);

  // attitudeDisplayParamsからパラメータを取り出す
  const { safeAttitudeRocketParams, cogToBottom } = attitudeDisplayParams;

  // ロケットのスケールを安全に取得
  const rocketScale = getSafeNumber(rocketSim.rocketScale, 0.5);
  const rocketDisplayParams = {
    bodyWidth: getSafeNumber(rocketSim.bodyWidth * rocketScale, 15),
    bodyHeight: getSafeNumber(rocketSim.bodyHeight * rocketScale, 100),
    noseHeight: getSafeNumber(rocketSim.noseHeight * rocketScale, 25),
    finHeight: getSafeNumber(rocketSim.finHeight * rocketScale, 20),
    finBaseWidth: getSafeNumber(rocketSim.finBaseWidth * rocketScale, 15),
    finTipWidth: getSafeNumber(rocketSim.finTipWidth * rocketScale, 10),
    finThickness: getSafeNumber(rocketSim.finThickness * rocketScale, 1),
    finSweepLength: getSafeNumber(rocketSim.finSweepLength * rocketScale, 2.5)
  };

  // 表示時に使用する安全なメソッド
  const metersToSvgX = (x) => {
    return rocketSim.metersToSvgX ? rocketSim.metersToSvgX(x) : 400 + x * 10;
  };

  const metersToSvgY = (y) => {
    return rocketSim.metersToSvgY ? rocketSim.metersToSvgY(y) : 550 - y * 10;
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <div className="mb-4">
        <h3 className="text-xl font-bold mb-2">飛行シミュレーション</h3>

        <div className="grid grid-cols-2 gap-6 mb-4">
          <div>
            <div className="mb-4">
              <label className="block mb-2">発射角度 (度)</label>
              <input
                ref={rocketSim.launchAngleInputRef}
                type="range"
                value={getSafeNumber(rocketSim.launchAngle)}
                min="-30"
                max="30"
                step="1"
                disabled={rocketSim.isLaunched}
                {...(rocketSim.handleLaunchAngle || {})}
                className="w-full cursor-pointer"
              />
              <div className="flex justify-between">
                <span>-30°</span>
                <span className="font-medium">{getSafeNumber(rocketSim.launchAngle)}°</span>
                <span>30°</span>
              </div>
            </div>

            <div className="mb-4">
              <label className="block mb-2">横風速度 (m/s)</label>
              <input
                ref={rocketSim.windSpeedInputRef}
                type="range"
                value={safeWindSpeed}
                min="-8"
                max="8"
                step="0.1"
                disabled={rocketSim.isLaunched}
                {...(rocketSim.handleWindSpeed || {})}
                className="w-full cursor-pointer"
              />
              <div className="flex justify-between">
                <span>-8 m/s</span>
                <span className="font-medium">{safeWindSpeed.toFixed(1)} m/s</span>
                <span>8 m/s</span>
              </div>
            </div>

            <div className="mt-4">
              <label className="block mb-2">風速プロファイル</label>
              <div className="flex items-center gap-4">
                <select
                  value={rocketSim.windProfile || 'uniform'}
                  onChange={(e) => rocketSim.setWindProfile && rocketSim.setWindProfile(e.target.value)}
                  disabled={rocketSim.isLaunched}
                  className="p-2 border border-gray-300 rounded w-48"
                >
                  {Object.entries(WIND_PROFILES || {}).map(([key, profile]) => (
                    <option key={key} value={key}>
                      {profile.name}
                    </option>
                  ))}
                </select>

                <div className="flex items-center">
                  <input
                    type="checkbox"
                    id="showWindArrows"
                    checked={rocketSim.showWindArrows || false}
                    onChange={(e) => rocketSim.setShowWindArrows && rocketSim.setShowWindArrows(e.target.checked)}
                    className="mr-2"
                  />
                  <label htmlFor="showWindArrows">風速分布表示</label>
                </div>
              </div>

              <div className="mt-2 text-sm text-gray-600">
                <p>高度が上がるにつれて風速が強くなります。地形によって変化率が異なります。</p>
                {rocketSim.windProfile !== 'uniform' && safeWindSpeed !== 0 && (
                  <p className="mt-1">
                    <span className="font-medium">現在の設定:</span> 地上風速 {safeWindSpeed.toFixed(1)} m/s、
                    高度80mでは約 {calculateWindSpeedAtHeight(safeWindSpeed, 80, rocketSim.windProfile || 'uniform').toFixed(1)} m/s
                  </p>
                )}
              </div>
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4 text-sm">
            <div className="space-y-2">
              <div>現在時刻: {getSafeNumber(rocketSim.currentTime).toFixed(2)} s</div>
              <div>現在高度: {getSafeNumber(rocketSim.currentHeight).toFixed(1)} m</div>
              <div>現在速度: {getSafeNumber(rocketSim.currentSpeed).toFixed(1)} m/s</div>
              <div>現在水平距離: {getSafeNumber(rocketSim.currentDistance).toFixed(1)} m</div>
              <div>フィンたわみ量: {formatFinDeflection(getSafeNumber(rocketSim.currentFinDeflection))}</div>
              {safePosition.effectiveWindSpeed !== undefined && (
                <div>現在実効風速: {getSafeNumber(safePosition.effectiveWindSpeed).toFixed(1)} m/s</div>
              )}
            </div>
            <div className="space-y-2">
              <div>最高到達高度: {getSafeNumber(rocketSim.currentMaxHeight).toFixed(1)} m</div>
              <div>最高速度: {getSafeNumber(rocketSim.currentMaxSpeed).toFixed(1)} m/s</div>
              <div>最大水平距離: {getSafeNumber(rocketSim.currentMaxDistance).toFixed(1)} m</div>
              <div>最大フィンたわみ量: {getSafeNumber(rocketSim.currentMaxFinDeflection).toFixed(2)} mm</div>
            </div>
          </div>
        </div>

        <div className="flex justify-between mb-4">
          <div className="space-x-2">
            <button 
              onClick={rocketSim.handleLaunch}
              disabled={rocketSim.isLaunched}
              className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 disabled:opacity-50"
            >
              発射
            </button>
            <button 
              onClick={rocketSim.handleReset}
              className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
            >
              リセット
            </button>
            <button
              onClick={() => setDebugView(!debugView)}
              className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
            >
              {debugView ? "通常表示" : "デバッグ表示"}
            </button>
            {(rocketSim.completedFlights || []).length > 0 && (
              <span className="text-sm text-gray-600 ml-2 italic">
                {(rocketSim.completedFlights || []).length}回の軌跡を表示中
              </span>
            )}
          </div>
        </div>

        {/* シミュレーション表示エリアを横並びに変更 */}
        <div className="flex gap-4">
          {/* 左側: 飛行軌跡表示 */}
          <div className="relative w-full bg-white rounded-lg border">
            <svg 
              width="100%" 
              height={600} 
              viewBox={`0 0 800 600`}
              style={{ background: 'white' }}
            >
              {/* グリッド線 - 常に表示するよう変更 */}
              <>
                {/* 横線 - 高度メモリ */}
                {Array.from({ length: 10 }).map((_, i) => {
                  const heightMeters = i * 10; // 10mごとに線を引く
                  const y = metersToSvgY(heightMeters);

                  if (y >= 0 && y <= 550) {
                    return (
                      <g key={`grid-h-${i}`}>
                        <line 
                          x1={0} 
                          y1={y} 
                          x2={800} 
                          y2={y} 
                          stroke="#ddd" 
                          strokeWidth="1" 
                          strokeDasharray="5,5" 
                        />
                        <text x={5} y={y - 5} fontSize="12" fill="#666">
                          {heightMeters}m
                        </text>
                      </g>
                    );
                  }
                  return null;
                })}

                {/* 縦線 - 水平距離メモリ */}
                {Array.from({ length: 11 }).map((_, i) => {
                  const distanceMeters = (i - 5) * 10; // -50mから+50mまで
                  const x = metersToSvgX(distanceMeters);

                  if (x >= 0 && x <= 800) {
                    return (
                      <g key={`grid-v-${i}`}>
                        <line 
                          x1={x} 
                          y1={0} 
                          x2={x} 
                          y2={600} 
                          stroke="#ddd" 
                          strokeWidth="1" 
                          strokeDasharray="5,5" 
                        />
                        {distanceMeters !== 0 && (
                          <text x={x + 5} y={550 + 15} fontSize="12" fill="#666">
                            {distanceMeters}m
                          </text>
                        )}
                      </g>
                    );
                  }
                  return null;
                })}
              </>

              {/* 風速の視覚的表示 */}
              {(debugView || safeWindSpeed !== 0) && (
                <g>
                  <text 
                    x={20} 
                    y={30} 
                    fontSize="16" 
                    fill={safeWindSpeed === 0 ? "#999" : "#333"}
                    fontWeight={Math.abs(safeWindSpeed) > 2 ? "bold" : "normal"}
                  >
                    風速: {safeWindSpeed.toFixed(1)} m/s
                  </text>

                  {/* 風向き矢印 - テキストに合わせて配置 */}
                  {safeWindSpeed !== 0 && windArrow && (
                    <g>
                      {/* 矢印の線 */}
                      <path 
                        d={windArrow.line} 
                        stroke={windArrow.color}
                        strokeWidth={windArrow.strokeWidth} 
                        fill="none"
                      />
                      {/* 矢印の頭 */}
                      <path 
                        d={windArrow.head}
                        stroke={windArrow.color}
                        strokeWidth={windArrow.strokeWidth} 
                        fill="none"
                      />
                    </g>
                  )}
                </g>
              )}

              {/* 風速分布の矢印表示 - 矢印の位置を修正、四角形を削除 */}
              {rocketSim.showWindArrows && safeWindSpeed !== 0 && rocketSim.getWindProfileArrows && (
                <g>
                  <text x={30} y={100} fontSize="12" fill="#666" fontWeight="bold">
                    高度別風速
                  </text>

                  {rocketSim.getWindProfileArrows(safeWindSpeed, rocketSim.windProfile || 'uniform').map((arrow, index) => (
                    <g key={`wind-arrow-${index}`}>
                      <text x={30} y={arrow.y - 15} fontSize="10" fill="#666">
                        {arrow.height}m
                      </text>
                      <path
                        d={arrow.arrowPath.line}
                        stroke={arrow.arrowPath.color}
                        strokeWidth={arrow.arrowPath.strokeWidth}
                        fill="none"
                      />
                      <path
                        d={arrow.arrowPath.head}
                        stroke={arrow.arrowPath.color}
                        strokeWidth={arrow.arrowPath.strokeWidth}
                        fill="none"
                      />
                      <text x={arrow.arrowPath.textX} y={arrow.arrowPath.textY + 4} fontSize="10" fill="#666">
                        {arrow.windSpeed.toFixed(1)} m/s
                      </text>
                    </g>
                  ))}
                </g>
              )}

              {/* 地面 */}
              <rect 
                x={0} 
                y={550} 
                width={800} 
                height={50} 
                fill="#4b5563" 
              />

              {/* 発射台 */}
              <g transform={`rotate(${getSafeNumber(rocketSim.launchAngle)} 400 550)`}>
                <line 
                  x1={380} 
                  y1={550} 
                  x2={420} 
                  y2={550} 
                  stroke="#374151" 
                  strokeWidth="4" 
                />
                <line 
                  x1={400} 
                  y1={550} 
                  x2={400} 
                  y2={550 - 0.65 * getSafeNumber(rocketSim.trajectoryScale, 10)} 
                  stroke="#374151" 
                  strokeWidth="2" 
                />
              </g>

              {/* 過去のフライト軌跡 - データ間引きを減らして密度を上げる */}
              {(rocketSim.completedFlights || []).map((flight, index) => (
                <path
                  key={`completed-flight-${index}`}
                  d={`M 400 550 ` + 
                    (flight.data || [])
                      .filter((d, i) => i % 2 === 0) // データ間引きをさらに減らす
                      .map(d => {
                        const x = 400 + getSafeNumber(d.physicsX) * getSafeNumber(flight.scale, 10);
                        const y = 550 - getSafeNumber(d.physicsY) * getSafeNumber(flight.scale, 10);
                        return `L ${x} ${y}`;
                      })
                      .join(' ')}
                  stroke="rgba(107, 114, 128, 0.5)"
                  strokeWidth="1.5"
                  fill="none"
                  strokeDasharray="2 2"
                />
              ))}

              {/* 現在のフライト軌跡 - 色を鮮やかに、線を太く */}
              {rocketSim.isLaunched && (rocketSim.flightData || []).length > 0 && (
                <path
                  d={`M 400 550 ` + 
                    (rocketSim.flightData || [])
                      .filter((d, i) => i <= Math.floor(getSafeNumber(rocketSim.currentTime) / 0.02) && i % 2 === 0)
                      .map(d => {
                        // NaNチェックを追加
                        const x = isNaN(d.physicsX) ? 400 : 400 + d.physicsX * getSafeNumber(rocketSim.trajectoryScale, 10);
                        const y = isNaN(d.physicsY) ? 550 : 550 - d.physicsY * getSafeNumber(rocketSim.trajectoryScale, 10);
                        return `L ${x} ${y}`;
                      })
                      .join(' ')}
                  stroke="#2563eb"
                  strokeWidth="2.5"
                  fill="none"
                />
              )}

              {/* ロケットとパラシュートの表示 - パラシュート表示を改良 */}
              {rocketSim.isLaunched && (rocketSim.flightData || []).length > 0 && (
                <>
                  {/* パラシュート表示（パラシュート展開時のみ） */}
                  {safePosition.isParachuteEjected && rocketSim.getParachuteStringPaths && rocketSim.getParachutePath && (
                    <>
                      <path
                        d={rocketSim.getParachuteStringPaths(
                          metersToSvgX(safePosition.physicsX || 0), 
                          metersToSvgY(safePosition.physicsY || 0), 
                          safePosition.isParachuteActive || false, 
                          getSafeNumber(safePosition.parachuteDeploymentProgress),
                          safeRotation
                        )}
                        stroke="#374151"
                        strokeWidth="1"
                        fill="none"
                      />
                      <path
                        d={rocketSim.getParachutePath(
                          metersToSvgX(safePosition.physicsX || 0), 
                          metersToSvgY(safePosition.physicsY || 0), 
                          safePosition.isParachuteActive || false, 
                          getSafeNumber(safePosition.parachuteDeploymentProgress),
                          safeRotation
                        )}
                        fill={safePosition.isParachuteActive ? "#FFB300" : "#9ca3af"}
                        stroke="#374151"
                        strokeWidth="2"
                      />
                    </>
                  )}

                  {/* ロケット本体 */}
                  <g transform={rocketSim.getSafeRotationTransform ? 
                    rocketSim.getSafeRotationTransform(
                      safeRotation, 
                      metersToSvgX(safePosition.physicsX || 0), 
                      metersToSvgY(safePosition.physicsY || 0)
                    ) : `translate(${metersToSvgX(safePosition.physicsX || 0)}, ${metersToSvgY(safePosition.physicsY || 0)}) rotate(${safeRotation})`}>
                    {rocketSim.getBodyPath && (
                      <path 
                        d={rocketSim.getBodyPath(
                          {
                            height: 0,
                            centerX: metersToSvgX(safePosition.physicsX || 0)
                          }, 
                          rocketDisplayParams.bodyHeight,
                          rocketDisplayParams.bodyWidth
                        )} 
                        fill="#9CA3AF" 
                        stroke="#374151" 
                      />
                    )}

                    {rocketSim.getLeftFinPath && (
                      <path 
                        d={rocketSim.getLeftFinPath(
                          {
                            height: 0,
                            centerX: metersToSvgX(safePosition.physicsX || 0)
                          }, 
                          rocketDisplayParams.bodyWidth,
                          rocketDisplayParams.finHeight,
                          rocketDisplayParams.finBaseWidth,
                          rocketDisplayParams.finSweepLength,
                          rocketDisplayParams.finTipWidth
                        )} 
                        fill="#6B7280" 
                        stroke="#374151" 
                      />
                    )}

                    {rocketSim.getRightFinPath && (
                      <path 
                        d={rocketSim.getRightFinPath(
                          {
                            height: 0,
                            centerX: metersToSvgX(safePosition.physicsX || 0)
                          }, 
                          rocketDisplayParams.bodyWidth,
                          rocketDisplayParams.finHeight,
                          rocketDisplayParams.finBaseWidth,
                          rocketDisplayParams.finSweepLength,
                          rocketDisplayParams.finTipWidth
                        )} 
                        fill="#6B7280" 
                        stroke="#374151" 
                      />
                    )}

                    {rocketSim.getCenterFinsPath && (
                      <path 
                        d={rocketSim.getCenterFinsPath(
                          {
                            height: 0,
                            centerX: metersToSvgX(safePosition.physicsX || 0)
                          }, 
                          rocketDisplayParams.finThickness,
                          rocketDisplayParams.finBaseWidth,
                          rocketDisplayParams.finSweepLength,
                          rocketDisplayParams.finTipWidth
                        )} 
                        fill="#6B7280" 
                        stroke="#374151" 
                      />
                    )}

                    {rocketSim.getNosePath && (
                      <path 
                        d={rocketSim.getNosePath(
                          {
                            height: 0,
                            centerX: metersToSvgX(safePosition.physicsX || 0)
                          }, 
                          rocketSim.noseShape || "cone",
                          rocketDisplayParams.noseHeight,
                          rocketDisplayParams.bodyWidth,
                          rocketDisplayParams.bodyHeight
                        )} 
                        fill="#D1D5DB" 
                        stroke="#374151" 
                      />
                    )}
                  </g>
                </>
              )}

              {/* 未発射時のロケット表示 */}
              {!rocketSim.isLaunched && (
                <g transform={`rotate(${getSafeNumber(rocketSim.launchAngle)} 400 550)`}>
                  {/* ボディ */}
                  <rect
                    x={400 - rocketDisplayParams.bodyWidth / 2}
                    y={550 - rocketDisplayParams.bodyHeight}
                    width={rocketDisplayParams.bodyWidth}
                    height={rocketDisplayParams.bodyHeight}
                    fill="#9CA3AF"
                    stroke="#374151"
                    strokeWidth="1"
                  />

                  {/* ノーズ */}
                  <path
                    d={`M ${400 - rocketDisplayParams.bodyWidth / 2} ${550 - rocketDisplayParams.bodyHeight} 
                        L ${400} ${550 - rocketDisplayParams.bodyHeight - rocketDisplayParams.noseHeight} 
                        L ${400 + rocketDisplayParams.bodyWidth / 2} ${550 - rocketDisplayParams.bodyHeight} Z`}
                    fill="#D1D5DB"
                    stroke="#374151"
                    strokeWidth="1"
                  />

                  {/* 左フィン */}
                  <path
                    d={`M ${400 - rocketDisplayParams.bodyWidth / 2} ${550 - rocketDisplayParams.finBaseWidth}
                        L ${400 - rocketDisplayParams.bodyWidth / 2} ${550}
                        L ${400 - rocketDisplayParams.bodyWidth / 2 - rocketDisplayParams.finHeight} ${550 + rocketDisplayParams.finSweepLength}
                        L ${400 - rocketDisplayParams.bodyWidth / 2 - rocketDisplayParams.finHeight} ${550 + rocketDisplayParams.finSweepLength - rocketDisplayParams.finTipWidth} Z`}
                    fill="#6B7280"
                    stroke="#374151"
                    strokeWidth="1"
                  />

                  {/* 右フィン */}
                  <path
                    d={`M ${400 + rocketDisplayParams.bodyWidth / 2} ${550 - rocketDisplayParams.finBaseWidth}
                        L ${400 + rocketDisplayParams.bodyWidth / 2} ${550}
                        L ${400 + rocketDisplayParams.bodyWidth / 2 + rocketDisplayParams.finHeight} ${550 + rocketDisplayParams.finSweepLength}
                        L ${400 + rocketDisplayParams.bodyWidth / 2 + rocketDisplayParams.finHeight} ${550 + rocketDisplayParams.finSweepLength - rocketDisplayParams.finTipWidth} Z`}
                    fill="#6B7280"
                    stroke="#374151"
                    strokeWidth="1"
                  />

                  {/* センターフィン */}
                  <rect
                    x={400 - rocketDisplayParams.finThickness / 2}
                    y={550 - rocketDisplayParams.finBaseWidth}
                    width={rocketDisplayParams.finThickness}
                    height={rocketDisplayParams.finBaseWidth}
                    fill="#6B7280"
                    stroke="#374151"
                    strokeWidth="1"
                  />
                </g>
              )}

              {/* デバッグ情報表示 */}
              {debugView && (
                <g>
                  <rect
                    x={10}
                    y={10}
                    width={280}
                    height={380}
                    fill="rgba(255,255,255,0.8)"
                    stroke="#666"
                    strokeWidth="1"
                  />
                  <text x={20} y={30} fontSize="12" fill="#333">
                    時間: {getSafeNumber(rocketSim.currentTime).toFixed(2)} 秒
                  </text>
                  <text x={20} y={50} fontSize="12" fill="#333">
                    高度: {getSafeNumber(rocketSim.currentHeight).toFixed(2)} m
                  </text>
                  <text x={20} y={70} fontSize="12" fill="#333">
                    速度: {getSafeNumber(rocketSim.currentSpeed).toFixed(2)} m/s
                  </text>
                  <text x={20} y={90} fontSize="12" fill="#333">
                    角度: {(safeRotation % 360).toFixed(2)}°
                  </text>
                  <text x={20} y={110} fontSize="12" fill="#333">
                    現在水平距離: {getSafeNumber(rocketSim.currentDistance).toFixed(2)} m
                  </text>
                  <text x={20} y={130} fontSize="12" fill="#333">
                    フェーズ: {rocketSim.getCurrentFlightPhase ? rocketSim.getCurrentFlightPhase() : "不明"}
                  </text>
                  <text x={20} y={150} fontSize="12" fill="#333">
                    パラシュート展開率: {getSafeNumber(safePosition.parachuteDeploymentProgress).toFixed(2)}
                  </text>
                  <text x={20} y={170} fontSize="12" fill="#333">
                    アニメーション速度: 0.2倍速
                  </text>
                  <text x={20} y={190} fontSize="12" fill="#333">
                    推力終了時高度: {(rocketSim.keyPoints?.thrustEnd?.height || 0).toFixed(2) || "N/A"} m
                  </text>
                  <text x={20} y={210} fontSize="12" fill="#333">
                    横風速度: {safeWindSpeed.toFixed(2)} m/s
                  </text>
                  {safePosition.effectiveWindSpeed !== undefined && (
                    <text x={20} y={230} fontSize="12" fill="#333">
                      実効風速: {getSafeNumber(safePosition.effectiveWindSpeed).toFixed(2)} m/s
                    </text>
                  )}
                  <text x={20} y={250} fontSize="12" fill="#333">
                    最大水平距離: {getSafeNumber(rocketSim.currentMaxDistance).toFixed(2)} m
                  </text>
                  <text x={20} y={270} fontSize="12" fill="#333">
                    表示スケール: {getSafeNumber(rocketSim.trajectoryScale, 10).toFixed(2)} px/m
                  </text>
                  <text x={20} y={290} fontSize="12" fill="#333">
                    ロケットスケール: {rocketScale.toFixed(5)}
                  </text>
                  <text x={20} y={310} fontSize="12" fill="#333">
                      フィンたわみ量: {getSafeNumber(safePosition.finDeflection).toFixed(2)} mm
                  </text>
                  {safePosition.torque !== undefined && (
                    <text x={20} y={330} fontSize="12" fill="#333">
                      トルク: {getSafeNumber(safePosition.torque).toFixed(6)}
                    </text>
                  )}
                  <text x={20} y={350} fontSize="12" fill="#333">
                    風向き: {safeWindSpeed > 0 ? "左→右" : safeWindSpeed < 0 ? "右→左" : "無風"}
                  </text>
                  <text x={20} y={370} fontSize="12" fill="#333">
                    角度制限: {safeWindSpeed !== 0 ? `${-Math.sign(safeWindSpeed) * 90}°` : "なし"}
                  </text>
                </g>
              )}
            </svg>
          </div>

          {/* 右側: 新しい円形の姿勢表示窓 */}
          <div className="w-1/4 bg-white rounded-lg border p-4 flex flex-col items-center">
            <h4 className="font-semibold mb-2">ロケット姿勢表示</h4>

            {/* 円形の窓 */}
            <div className="relative">
              <svg 
                width={200} 
                height={200} 
                viewBox="-100 -100 200 200"
                className="border-2 border-gray-300 rounded-full overflow-hidden bg-gray-50"
              >
                {/* 背景の円 */}
                <circle cx="0" cy="0" r="98" fill="#f0f0f0" />

                {/* 角度目盛り */}
                {[...Array(12)].map((_, i) => {
                  const angle = i * 30;
                  const radians = (angle - 90) * Math.PI / 180;
                  const x1 = Math.cos(radians) * 80;
                  const y1 = Math.sin(radians) * 80;
                  const x2 = Math.cos(radians) * 90;
                  const y2 = Math.sin(radians) * 90;

                  return (
                    <g key={`mark-${i}`}>
                      <line 
                        x1={x1} 
                        y1={y1} 
                        x2={x2} 
                        y2={y2} 
                        stroke="#999" 
                        strokeWidth="1" 
                      />
                      {i % 3 === 0 && (
                        <text 
                          x={Math.cos(radians) * 70} 
                          y={Math.sin(radians) * 70} 
                          textAnchor="middle" 
                          dominantBaseline="middle"
                          fontSize="10"
                          fill="#666"
                        >
                          {angle}°
                        </text>
                      )}
                    </g>
                  );
                })}

                {/* 水平線と垂直線 */}
                <line x1="-90" y1="0" x2="90" y2="0" stroke="#666" strokeWidth="1" strokeDasharray="3,2" />
                <line x1="0" y1="-90" x2="0" y2="90" stroke="#666" strokeWidth="1" strokeDasharray="3,2" />

                {/* ロケット - 姿勢角度を表示（重心を中心に回転） */}
                <g transform={`rotate(${safeRotation})`}>
                  {/* ロケット本体 - Y位置をずらして重心が原点に来るようにする */}
                  <g transform={`translate(0, ${-getSafeNumber(cogToBottom/2, 0)})`}>
                    {/* ボディ */}
                    <rect
                      x={-safeAttitudeRocketParams.bodyWidth / 2}
                      y={-safeAttitudeRocketParams.bodyHeight}
                      width={safeAttitudeRocketParams.bodyWidth}
                      height={safeAttitudeRocketParams.bodyHeight}
                      fill="#9CA3AF"
                      stroke="#374151"
                      strokeWidth="0.5"
                    />

                    {/* ノーズ */}
                    <path
                      d={`M ${-safeAttitudeRocketParams.bodyWidth / 2} ${-safeAttitudeRocketParams.bodyHeight} 
                          L 0 ${-safeAttitudeRocketParams.bodyHeight - safeAttitudeRocketParams.noseHeight} 
                          L ${safeAttitudeRocketParams.bodyWidth / 2} ${-safeAttitudeRocketParams.bodyHeight} Z`}
                      fill="#D1D5DB"
                      stroke="#374151"
                      strokeWidth="0.5"
                    />

                    {/* 左フィン */}
                    <path
                      d={`M ${-safeAttitudeRocketParams.bodyWidth / 2} ${-safeAttitudeRocketParams.finBaseWidth}
                          L ${-safeAttitudeRocketParams.bodyWidth / 2} ${0}
                          L ${-safeAttitudeRocketParams.bodyWidth / 2 - safeAttitudeRocketParams.finHeight} ${safeAttitudeRocketParams.finSweepLength}
                          L ${-safeAttitudeRocketParams.bodyWidth / 2 - safeAttitudeRocketParams.finHeight} ${safeAttitudeRocketParams.finSweepLength - safeAttitudeRocketParams.finTipWidth} Z`}
                      fill="#6B7280"
                      stroke="#374151"
                      strokeWidth="0.5"
                    />

                    {/* 右フィン */}
                    <path
                      d={`M ${safeAttitudeRocketParams.bodyWidth / 2} ${-safeAttitudeRocketParams.finBaseWidth}
                          L ${safeAttitudeRocketParams.bodyWidth / 2} ${0}
                          L ${safeAttitudeRocketParams.bodyWidth / 2 + safeAttitudeRocketParams.finHeight} ${safeAttitudeRocketParams.finSweepLength}
                          L ${safeAttitudeRocketParams.bodyWidth / 2 + safeAttitudeRocketParams.finHeight} ${safeAttitudeRocketParams.finSweepLength - safeAttitudeRocketParams.finTipWidth} Z`}
                      fill="#6B7280"
                      stroke="#374151"
                      strokeWidth="0.5"
                    />

                    {/* センターフィン */}
                    <rect
                      x={-safeAttitudeRocketParams.finThickness / 2}
                      y={-safeAttitudeRocketParams.finBaseWidth}
                      width={safeAttitudeRocketParams.finThickness}
                      height={safeAttitudeRocketParams.finBaseWidth}
                      fill="#6B7280"
                      stroke="#374151"
                      strokeWidth="0.5"
                    />

                    {/* 重心位置のマーカー */}
                    <circle cx="0" cy="0" r="2" fill="red" />
                  </g>
                </g>

                {/* 現在の角度表示 */}
                <text 
                  x="0" 
                  y="-85" 
                  textAnchor="middle" 
                  fontSize="12" 
                  fill="#333"
                  fontWeight="bold"
                >
                  {(safeRotation % 360).toFixed(1)}°
                </text>
              </svg>
            </div>

            {/* 姿勢に関する追加情報 */}
            <div className="mt-4 text-sm">
              <div className="grid grid-cols-2 gap-x-2 gap-y-1">
                <div className="font-semibold">飛行フェーズ:</div>
                <div>{rocketSim.getCurrentFlightPhase ? rocketSim.getCurrentFlightPhase() : "初期状態"}</div>

                <div className="font-semibold">発射角度:</div>
                <div>{getSafeNumber(rocketSim.launchAngle)}°</div>

                <div className="font-semibold">現在角度:</div>
                <div>{(safeRotation % 360).toFixed(1)}°</div>

                <div className="font-semibold">角度変化:</div>
                <div>{(safeRotation - getSafeNumber(rocketSim.launchAngle)).toFixed(1)}°</div>

                <div className="font-semibold">風向き:</div>
                <div>{safeWindSpeed > 0 ? "右" : safeWindSpeed < 0 ? "左" : "なし"}</div>
              </div>
            </div>
        {/* 前回の飛翔結果 */}
        {rocketSim.lastFlightResults && (
          <LastFlightResults results={rocketSim.lastFlightResults} />
        )}
      </div>
        </div>

        <div className="bg-white rounded-lg shadow-sm mt-4 p-4 border border-gray-200">
          <h4 className="font-semibold mb-2">使用パラメータ</h4>
          <div className="grid grid-cols-2 gap-4 text-sm">
            <ul className="list-disc pl-5">
              <li>重量: {rocketSim.weight} g</li>
              <li>モーター: {rocketSim.selectedMotor}</li>
              <li>パラシュート: {rocketSim.selectedParachute}</li>
              <li>ノーズ形状: {NOSE_SHAPES[rocketSim.noseShape].name}</li>
              <li>抗力係数: {NOSE_SHAPES[rocketSim.noseShape].cd.toFixed(2)}</li>
            </ul>
            <ul className="list-disc pl-5">
              <li>ロケット全長: {rocketSim.calculations.totalHeight} mm</li>
              <li>ボディ直径: {rocketSim.bodyWidth} mm</li>
              <li>フィン翼幅: {rocketSim.finHeight} mm</li>
              <li>フィン厚み: {rocketSim.finThickness.toFixed(1)} mm</li>
              <li>フィン材質: {FIN_MATERIALS[rocketSim.finMaterial].name}</li>
              <li>重心位置: {rocketSim.centerOfGravity} mm</li>
              <li>風速プロファイル: {WIND_PROFILES[rocketSim.windProfile].name}</li>
            </ul>
          </div>
        </div>
      </div>

      {/* 結果ポップアップ */}
      {rocketSim.showResultsPopup && (
        <ResultsPopup 
          results={rocketSim.flightResults} 
          onClose={rocketSim.handleCloseResultsPopup} 
        />
      )}
    </div>
  );
};
